use Joomla\CMS\Factory;

class ModOpenaidalleHelper
{
	public static function getDataAjax()
	{
		$tipo="data:image/png;base64,";
		$immagine ="";
		$data = $tipo . $immagine;
		$source = fopen($data, 'r');
		$destination = fopen('testimg02.png', 'w');

		stream_copy_to_stream($source, $destination);

		fclose($source);
		fclose($destination);
	}
}